function updateTime(){const now=new Date();const daysOfWeek=["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"];const dayOfWeek=daysOfWeek[now.getDay()];const hours=String(now.getHours()).padStart(2,'0');const minutes=String(now.getMinutes()).padStart(2,'0');const day=String(now.getDate()).padStart(2,'0');const month=String(now.getMonth()+1).padStart(2,'0');const year=String(now.getFullYear());const formattedTime=`${hours}:${minutes}${' '}${year}/${month}/${day}${' '}${dayOfWeek}`;document.getElementById('time').textContent=formattedTime}setInterval(updateTime,30000);updateTime();function showModal(modalId){document.getElementById(modalId).style.display='flex'}function closeModal(modalId){document.getElementById(modalId).style.display='none'}function showModalWithFile(modalId,outTextId,fileName){const uniqueId=outTextId.split('-')[1];const loadingId='loading-'+uniqueId;const loading=document.getElementById(loadingId);showModal(modalId);loading.style.display='flex';setTimeout(()=>{fetch(fileName).then(response=>response.text()).then(data=>{loading.style.display='none';document.getElementById(outTextId).innerHTML=data}).catch(error=>{console.error('Error fetching file:',error);loading.style.display='none';document.getElementById(outTextId).innerText='Error loading file.'})},500)}window.onclick=function(event){var modals=document.querySelectorAll('.modal');modals.forEach(modal=>{if(event.target===modal){closeModal(modal.id)}})};document.addEventListener('keydown',function(event){if(event.key==='Escape'||event.keyCode===27){document.querySelectorAll('.modal').forEach(modal=>{if(modal.style.display==='flex'){closeModal(modal.id)}})}});const viewsHome="72";const viewsResearch="59";const viewsEducation="46";const lastUpdated="December 2, 2024";function displayLastUpdatedData(){const HomeElement=document.getElementById("viewsHome");const dateElement=document.getElementById("lastUpdatedDate");const ResearchElement=document.getElementById("viewsResearch");const EducationElement=document.getElementById("viewsEducation");const iconViews=`<img src="files/images/logo/views.webp"width="9.5"height="9.5"style="vertical-align:-1px;margin-right:1px;">`;const iconUpdate=`<img src="files/images/logo/update.webp"width="9.5"height="9.5"style="vertical-align:-1px;margin-right:1.2px;">`;const analyticsLink=`<a href="https://analytics.google.com"target="_blank">Google Analytics</a>`;if(dateElement){dateElement.innerHTML=`${iconUpdate}${' '}Time of last update:${' '}${lastUpdated}`}if(HomeElement){HomeElement.innerHTML=`${iconViews}${' '}Pageviews:${' '}${viewsHome}${' '}(by ${analyticsLink})`}if(ResearchElement){ResearchElement.innerHTML=`${iconViews}${' '}Pageviews:${' '}${viewsResearch}${' '}(by ${analyticsLink})`}if(EducationElement){EducationElement.innerHTML=`${iconViews}${' '}Pageviews:${' '}${viewsEducation}${' '}(by ${analyticsLink})`}}window.onload=displayLastUpdatedData;var scrollToTopButton=document.getElementById("scrollToTop");var firstHeading=document.querySelector("section h2:first-of-type");var scrollToSelector=document.getElementById('scrollToSection');function isOutOfViewport(element){var rect=element.getBoundingClientRect();return(rect.top>=window.innerHeight||rect.bottom<0||rect.right<0||rect.left>=window.innerWidth)}function buttonVisibility(){if(isOutOfViewport(firstHeading)){scrollToTopButton.style.display="block"}else{scrollToTopButton.style.display="none"}if(window.innerWidth<1100){scrollToSelector.style.display="block"}else{scrollToSelector.style.display="block"}}function scrollToTop(){window.scrollTo({top:0,behavior:"smooth"})}function scrollToSection(sectionId){var section=document.getElementById(sectionId);if(section){var scrollPosition=section.offsetTop-150;if(scrollPosition<0){scrollPosition=0}window.scrollTo({top:scrollPosition,behavior:'smooth'})}}function topButtonPosition(){const section=document.querySelector('section');if(section){const sectionRect=section.getBoundingClientRect();let buttonRight=window.innerWidth-(sectionRect.right+200);buttonRight=Math.max(buttonRight,0);scrollToTopButton.style.right=`${buttonRight}px`}}function sectionButtonPosition(){const section=document.querySelector('section');if(section){const sectionRect=section.getBoundingClientRect();let buttonRight=window.innerWidth-(sectionRect.right+200);buttonRight=Math.max(buttonRight,0);scrollToSelector.style.right=`${buttonRight}px`}}window.addEventListener("scroll",buttonVisibility);scrollToTopButton.addEventListener("click",scrollToTop);window.addEventListener('resize',()=>{topButtonPosition();sectionButtonPosition();buttonVisibility()});document.addEventListener('DOMContentLoaded',()=>{topButtonPosition();sectionButtonPosition();buttonVisibility()});buttonVisibility();